<!-- Set Gift Drop Down Choice -->
<script>
    //bookmark animation
    $(".bookmarks a").on('click',function(e) {
        var url = $(this).attr('href');
        $('html, body').animate({
            scrollTop: $(url).offset().top
        }, 1000);
        return false;
    });


    // Variables Here
    Drupal.settings.lists                = {};
    Drupal.settings.lists.project = $(".project-list");
    function down (el) {
        el.next().slideDown();
        el.addClass('active');
    }

    function up (el) {
        el.next().slideUp();
        el.removeClass('active');
    }
    Drupal.settings.lists.project.click(function () {
        return (this.tog = !this.tog) ? down($(this)) : up($(this));
    });



</script>
